<script>
let count = 0
  let dom = document.querySelector('.container')
  let timeout
  function debounce(fun, time, immediate) {
    return function () {
      if (timeout) clearTimeout(timeout);
      if (immediate) {
        let callnow = !timeout
        timeout = setTimeout(() => {
          timeout = false
        }, time)
        if (callnow) {
          fun.apply(this, arguments)
        }
      } else {
        timeout = setTimeout(() => {
          fun.apply(this, arguments)
        }, time)
      }
    }

  }
export default {
  name: "App",
  data() {
    return {
      visible:false
    };
  },
  methods: {
    upup() {
      window.scrollTo({
        top: 0,
        behavior: "smooth",
      });
    },
  },
  
  created() {
    let that = this
    if (window.innerWidth <= 700) {
      window.onscroll = debounce(function () {
        //为了保证兼容性，这里取两个值，哪个有值取哪一个
        //scrollTop就是触发滚轮事件时滚轮的高度
        var scrollTop =
          document.documentElement.scrollTop || document.body.scrollTop;
        // console.log("滚动距离" + scrollTop);
         if(scrollTop >1000){
        that.visible = true
      }else{
        that.visible  = false
      }
      },200,false);
     
    } else {
      // scrollTop = null;
    }
  },
  components: {},
};
</script>
